export function getTotalSize() {
  return Object.values(mockItems).reduce((acc, cat) => acc + cat.totalSize, 0);
}

export function getCategorySizes() {
  return categories.map((cat) => ({
    ...cat,
    size: mockItems[cat.id]?.totalSize || 0,
  }));
}
